import React, { useState } from "react";

function App() {
  setInterval(times,1000)

  let time = new Date().toLocaleTimeString();
  let [val,valChange] = useState(time)

  const times = ()=>{
  let time = new Date().toLocaleTimeString();

  valChange(time);

  }
  return (
    <div className="container">
      <h1>{val}</h1>
      <button onClick={times}>Get Time</button>
    </div>
  );
}

export default App;
